import React from 'react';
import { useInView } from 'react-intersection-observer';
import './StatsSection.css';

const StatsSection: React.FC = () => {
  const { ref, inView } = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  return (
    <section ref={ref} className={`stats-section ${inView ? 'in-view' : ''}`}>
      <div className="container">
        <h2 className="stats-title">
          We <span className="highlight-golden">clarify</span> your brand messaging and help you <span className="highlight-golden">reach the right audience</span>
        </h2>
        <div className="stats-grid">
          <div>
            <div className="stat-number">150%</div>
            <div className="stat-label">Average ROI</div>
          </div>
          
          <div>
            <div className="stat-number">95%</div>
            <div className="stat-label">Client Retention</div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default StatsSection;
