import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import Header from './components/Header/Header';
import HeroSection from './components/HeroSection/HeroSection';
import StatsTitle from './components/StatsTitle/StatsTitle';
import StatsSection from './components/StatsSection/StatsSection';
import SolutionsSection from './components/SolutionsSection/SolutionsSection';
import ProcessSection from './components/ProcessSection/ProcessSection';
import CTASection from './components/CTASection/CTASection';
import CustomerSection from './components/CustomerSection/CustomerSection';
import TeamSection from './components/TeamSection/TeamSection';
import BuildReachGrowSection from './components/BuildReachGrowSection/BuildReachGrowSection';
import FAQSection from './components/FAQSection/FAQSection';
import Footer from './components/Footer/Footer';

function App() {
  const [scrollPercentage, setScrollPercentage] = useState(0);
  const mainRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleScroll = () => {
      const heroHeight = window.innerHeight;
      const scrollY = window.scrollY;
      const percentage = Math.min(100, (scrollY / heroHeight) * 100);
      setScrollPercentage(percentage);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const contentStyle = {
    transform: `translateY(${-scrollPercentage * 10}px)`,
  };

  return (
    <div className="App">
      <Header />
      <main className="main-content" ref={mainRef}>
        <HeroSection />
        <div className="content-wrapper" style={contentStyle}>
          <StatsTitle />
          <StatsSection />
          <SolutionsSection />
          <ProcessSection />
          <CTASection />
          <CustomerSection />
          <TeamSection />
          <BuildReachGrowSection />
          <FAQSection />
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default App;
