import React, { useEffect, useState, useRef } from 'react';
import './HeroSection.css';
import heroVideo from '../../assets/Rainbow_Motion_Video_Revision.mp4';

const HeroSection: React.FC = () => {
  const [offset, setOffset] = useState(0);
  const videoRef = useRef<HTMLVideoElement>(null);

  useEffect(() => {
    const handleScroll = () => {
      setOffset(window.pageYOffset);
    };

    window.addEventListener('scroll', handleScroll);

    const video = videoRef.current;
    if (video) {
      const handleTimeUpdate = () => {
        if (video.duration - video.currentTime < 0.3) {
          video.style.opacity = '0';
        }
      };

      const handleEnded = () => {
        video.currentTime = 0;
        video.style.opacity = '1';
        video.play();
      };

      video.addEventListener('timeupdate', handleTimeUpdate);
      video.addEventListener('ended', handleEnded);

      return () => {
        window.removeEventListener('scroll', handleScroll);
        video.removeEventListener('timeupdate', handleTimeUpdate);
        video.removeEventListener('ended', handleEnded);
      };
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <section id="home" className="hero-section">
      <div className="nectar__bg-video">
        <video ref={videoRef} autoPlay loop muted playsInline style={{ zIndex: 0, transition: 'opacity 0.3s ease-in-out' }}>
          <source src={heroVideo} type="video/mp4" />
        </video>
      </div>
      <div className="container">
        <div
          className="hero-content"
          style={{
            transform: `translateY(${offset * 0.4}px)`,
            opacity: 1 - offset / 500,
          }}
        >
          <div className="hero-text">
            <div className="hero-tag">
              <span></span>Full-Service
            </div>
            <h1 className="hero-title">
              Digital Marketing Agency
            </h1>
            
            <div className="hero-subtitle">
              <p className="hero-description">
                Build your brand. Reach your audience. Grow with results.
              </p>
            </div>
            <div className="hero-cta">
              <button className="btn btn-primary btn-large">
                Get Started
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
