import React, { useRef, useEffect, useState } from 'react';
import './HeroSection.css';
import backgroundVideo from '../../assets/48569-454825064_small.mp4';

const HeroSection: React.FC = () => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [scrollY, setScrollY] = useState(0);

  const handleScroll = () => {
    setScrollY(window.scrollY);
  };

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.playbackRate = 0.75;
    }
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scale = Math.max(0.5, 1 - scrollY / (window.innerHeight * 2));

  return (
    <section id="home" className="hero-section">
      <div className="hero-background" style={{ transform: `scaleY(${scale})` }}>
        <video ref={videoRef} autoPlay loop muted className="hero-video">
          <source src={backgroundVideo} type="video/mp4" />
        </video>
      </div>
      <div className="container">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Digital Marketing Agency
            </h1>
            
            <div className="hero-subtitle">
              <p className="hero-description">
                We <span className="highlight">clarify</span> your brand messaging and help you{' '}
                <span className="highlight">reach the right audience</span>
              </p>
            </div>
            
            <div className="hero-cta">
              <button className="btn btn-primary btn-large">
                Get Started
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
